(window.webpackJsonp=window.webpackJsonp||[]).push([[105],{395:function(t,_,v){"use strict";v.r(_);var a=v(46),r=Object(a.a)({},function(){var t=this,_=t.$createElement,v=t._self._c||_;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("h1",{attrs:{id:"_099"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_099","aria-hidden":"true"}},[t._v("#")]),t._v(" 099")]),t._v(" "),v("p",[t._v("脱走の罪で本来はアルジが入るはずだった真っ暗な懲罰房。今はケライがそこに閉じ込められている。腕を通した拘束衣は後ろで結ばれ，固定された椅子に鎖とともに縛りつけられている。舌を噛み切れないよう口には枷がはめられ，排泄と栄養の投与を兼ねるチューブが二本，体内の奥深くまで挿入され強烈な圧迫感を与えていた。これでもシッショのとっさの機転でかなり穏便に処置されたほうといえる。裏切り行為とはかくも許されぬものなのだ。シッショでなければ昼夜を問わず過酷な拷問もされていただろう。")]),t._v(" "),v("p",[t._v("仮にそれらが禁止されていたとしても，面白半分に薬剤を注入して腹を膨らせ苦しませたり，視界が封じられているのをいいことに大きな音を立てて怯えさせたり，侮蔑の言葉を投げかけるなど，心ないことをする者はいくらでもいる。")]),t._v(" "),v("p",[t._v("かつて，シッショの前ではおどけるばかりの明るい同僚が，裏では嬉々としてそうした残酷な仕打ちを行っているのを見た。シッショは人間というものがわからなくなった。祈りをささげるその腕で，平然と他人を傷つける。大切な者を慈しむ言葉を紡ぐその口で，お前は誰にも必要とされていない役立たずだと蔑む。あの嵐が全てを流さなければ，クビワというどこまでも純粋な存在に出会わなければ，自分もそうなっていたかもしれない。")]),t._v(" "),v("p",[t._v("そしてその黒い種は今でも消えずに残っている。")]),t._v(" "),v("br"),t._v(" "),v("p",[t._v("聞き込みを始めて間もなく，マッパから，ケライの立場を危うくする証言が出てきた。透かしのない資料を覗き見ることさえ拒否した，というものである。それはケライの文章が普段の言葉遣いや報告書とは似ても似つかないほどくだけた表現で，それを指摘されるのを嫌ったためだった。幸いそれが自然なことだとミミに言われたことで，シッショにはそのまま見せることができたのだが，それがかえって仇となった。つまり，オヤブンから調査にかんして全幅の信頼をおかれているマッパには見せたくなかったのだと。さらに，日記のような雑な文章しか書けないシッショを侮り，西方で務めていた経験豊富な人物であると気付かなかったのだと。")]),t._v(" "),v("p",[t._v("シッショはため息が絶えなかった。ケライ，どうしてそんな愚かなことを。シッショだってケライがスパイだとは毛頭考えていない。だが外部から物資が持ち込まれた確たる証拠があるのだ。潔白を証明するには，ケライがどこから手に取ったのか突き止める必要がある。各隊員の部屋にも立ち入らなければならない。疑心暗鬼を生むのは最大の損だ。とはいえ…")]),t._v(" "),v("p",[t._v("シッショは里の一角を眺めて気が遠くなる思いだった。書庫。シッショひとりで担当するにはあまりにも本が多すぎる。全ての本の全てのページを調べる？不可能だ。いや，不可能ではないが，何年かかるかわからない。")]),t._v(" "),v("p",[t._v("もって一週間。それがショムがシッショに伝えた制限時間だった。それを過ぎると，ケライが解放されてももとに戻らなくなってしまうだろう。万全の状態であればもっと耐えられるかもしれないが，ここ最近で，大切な人が傷ついたうえ，解雇を告げられ，不自由な環境で過ごし，再び受け入れられた直後の大きなトラブル，そしてスパイの嫌疑で拘束されたのだ。心身ともに疲れはてている状況でこんな仕打ちを受ければ，長くもたないのは明らかだ。")]),t._v(" "),v("p",[t._v("何のために人を苦しめてまでこんなことをやらなければいけないのか。孤独な戦いである。何度も思う。後悔もする。アルジと違ってケライはマニュアルを熟読したはずだ。そしてそこには，使用する紙に透かしが入っているか常に確認するように，とは書かれていなかった。シッショはそれを知っていた。なぜならそれこそがスパイをあぶり出すために仕掛けられたトラップのひとつだからだ。当然ながら罠は一つではない。支給された武器の柄に刻まれた透かし彫り，袋が開封されたか確認できるよう加工された接着テープ，特殊なライトを当てると発光するインクなど，オヤブンでさえ知らない隠された秘密によって，里は外部の侵入から守られているのだ。もちろん調査隊の規模は小さく，孤立している。こんなところに入ってこようとする物好きもいない。だからそれらの規則はもはや形だけのものとなっていた。")]),t._v(" "),v("p",[t._v("そのはずだった。")]),t._v(" "),v("p",[t._v("だがそれはケライの持つ数枚の紙によってぶち壊された。オヤブンが当初疑い，シッショの直感が否定した，アルジとケライがスパイであるという可能性が急速に現実味を帯びはじたのだ。ゆえに，アルジのいる治療室も頑丈な鍵で外から施錠されることになった。入室には常にシッショが同席する必要がある。もし危篤になってもその手続きは維持されるため，一分一秒を争う事態になれば，アルジの命が危険にさらされることにもなる。シッショは悪くない。決して破ってはいけない規則に従っているだけだ。だがそれが，アルジとケライ，二人の命を奪うことになるかもしれないのだ。シッショの葛藤ははかりしれず，それでもなおクビワに余計な心配をさせまいと気丈にふるまった。")]),t._v(" "),v("p",[t._v("シッショは紫針竜によって里が南からの連絡を絶たれていることを知らない。本来なら外部から侵入されることなどありえない。もし外部から侵入されたのだとすれば，それはアルジとケライが本当にスパイか，紫針竜の攻撃をかいくぐって南北を行き来する者がいるか，もしくは，北の大陸にアルジ達以外の人間がいるということになる。")]),t._v(" "),v("br"),t._v(" "),v("br"),t._v("  \n(c) 2018 jamcha (jamcha.aa@gmail.com).  \n"),v("p",[v("img",{attrs:{src:"https://i.creativecommons.org/l/by-nc-sa/4.0/88x31.png",alt:"cc by-nc-sa"}})])])},[],!1,null,null,null);_.default=r.exports}}]);