(window.webpackJsonp=window.webpackJsonp||[]).push([[172],{461:function(t,_,v){"use strict";v.r(_);var a=v(46),r=Object(a.a)({},function(){var t=this,_=t.$createElement,v=t._self._c||_;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("h1",{attrs:{id:"_166"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_166","aria-hidden":"true"}},[t._v("#")]),t._v(" 166")]),t._v(" "),v("p",[t._v("あくる日の夕方，一羽の巨鳥が里から北方に向け飛び立った。キセイの相棒，ビュンである。その足には，里が誇る最高の知性二人分の血肉が握られている。")]),t._v(" "),v("p",[t._v("比喩ではない。数日ぶりに医療施設から出てきたショムとミミは別人のように痩せ細り，目の落ちくぼんだ様子はさながら生ける屍のようであった。限られた期限のなか，一歩間違えば里を廃墟にしかねない危険生物を扱っていたのだ。よく生きて仕事を終えられたものだ。あとはビュンがキセイの指示したとおりの場所へ蝕霧を投下すれば，結果は自ずと知れる。")]),t._v(" "),v("p",[t._v("二人にとって不幸なのは，労をねぎらう者がアルジとケライしかいないことだ。どちらも口下手で礼儀を知らない。オヤブンは何を言っても二人を疲れさせることにしかならないし，キセイの仕事は里に残った全員を運ぶことなので，獣舎で過ごしている。本来はボッチやシンキがいればうまく対処できたのだが。")]),t._v(" "),v("p",[t._v("とはいえ四人の相性はさほど悪くないようだった。久し振りの酒が振る舞われ，軽い宴会になった。酔ったショムはケライにくだを巻き，昔の象牙の塔で起きた愚痴をさんざん聞かせ，かたやミミはアルジにぐいぐいと身体を押しつけながら，自分がどれだけひどく扱われてきたかを涙ながらに訴える。ケライはショムの文句に大いに同意するところがあるのか興味深く相槌をうち，酒を飲めないアルジはミミの思わぬアプローチにたじたじであった。")]),t._v(" "),v("p",[t._v("これだけ里が平穏な様子に戻るのもいつ以来だろうか。オヤブンは四者四様の喜劇に満足だった。")]),t._v(" "),v("br"),t._v(" "),v("p",[t._v("翌日，キセイの駆る獣車に必要な荷物を積み込み，六人は里を発った。裏山の作物が収穫できずに終わってしまったのは残念だが，地震がおさまって里再建の目処がたてば，また植えることができるだろう。")]),t._v(" "),v("p",[t._v("獣車のなかでショムはなおもケライとの学術的な話に花を咲かせ，二日酔いの抜けないミミは，水を飲みながらアルジに風を送ってもらっている。昨晩の記憶がほとんどなく，アルジに絡んでひどいことをしたのではないかと激しい自己嫌悪に陥っている。アルジは気にしないように言うが，ひどいめにあったのは事実である。宴会がおひらきになった後もとめどなく話しつづけるミミを，残って世話したのだ。それは汗と涙と涎と嘔吐と粗相が混在する試練であった。服を着替えさせ，寝かしつけてしばらく掃除をし，終わる頃には朝になっていた。")]),t._v(" "),v("p",[t._v("それに話の内容は多分に私的なものであり，アルジどころかオヤブンでさえ知らないような組織の深部に迫るものでもあった。名前も知らないような誰と誰が仲が悪いだとか，誰それが処刑されたのは実は誰それの策略だとか。泥酔した者の戯言であると強く願い，また普段は穏やかなミミがそうした噂に興味があることが意外であり，そしてそんな下品な噂が頭に焼きついて離れない自分自身にもあきれた。")]),t._v(" "),v("p",[t._v("ケライが大人しそうな見た目に反してかなりの武闘派であるように，ミミも見た目で判断してはいけないのだ，とミミの額で熱を測りながらアルジは改めて思った。")]),t._v(" "),v("p",[t._v("ふと獣車の上を何かが飛び去るのが見えた。敵ではない。ビュンだ。その足が空っぽなのを見て，無事仕事を終えたようだ。向かう先は森のキャンプ，すなわち新たな本部である。道中で紫針竜や天巾に捕まらなくてよかった，と安堵した。")]),t._v(" "),v("p",[t._v("やがて当初の数倍ほどの規模になったキャンプが視界に姿を現す。クビワが飛びはねながら手を振るのが見えた。")]),t._v(" "),v("br"),t._v(" "),v("br"),t._v("  \n(c) 2018 jamcha (jamcha.aa@gmail.com).  \n"),v("p",[v("img",{attrs:{src:"https://i.creativecommons.org/l/by-nc-sa/4.0/88x31.png",alt:"cc by-nc-sa"}})])])},[],!1,null,null,null);_.default=r.exports}}]);